CREATE VIEW geocaching_points_evw AS SELECT OBJECTID,Shape,Name,FolderPath,SymbolID,AltMode,Base,Snippet,PopupInfo,HasLabel,LabelID FROM geocaching_points WHERE gdb_to_date BETWEEN (julianday ('9999-12-31 23:59:59') - 0.000000001) AND (julianday ('9999-12-31 23:59:59') + 0.000000001);
CREATE TRIGGER geocaching_points_evw_delete INSTEAD OF DELETE ON geocaching_points_evw BEGIN DELETE FROM geocaching_points WHERE OBJECTID = OLD.OBJECTID AND gdb_from_date BETWEEN (julianday('now') - 0.000000001) AND (julianday('now') + 0.000000001); UPDATE OR REPLACE geocaching_points SET gdb_to_date = julianday('now') WHERE OBJECTID = OLD.OBJECTID AND gdb_to_date BETWEEN (julianday ('9999-12-31 23:59:59') - 0.000000001) AND (julianday ('9999-12-31 23:59:59') + 0.000000001); END;;
CREATE TRIGGER geocaching_points_evw_insert INSTEAD OF INSERT ON geocaching_points_evw BEGIN INSERT INTO geocaching_points (OBJECTID,Shape,Name,FolderPath,SymbolID,AltMode,Base,Snippet,PopupInfo,HasLabel,LabelID,gdb_from_date,gdb_to_date) VALUES (coalesce (NEW.OBJECTID,(select max(OBJECTID)+1 from 'geocaching_points'),1),NEW.Shape,NEW.Name,NEW.FolderPath,NEW.SymbolID,NEW.AltMode,NEW.Base,NEW.Snippet,NEW.PopupInfo,NEW.HasLabel,NEW.LabelID,julianday('now'),julianday ('9999-12-31 23:59:59')); END;;
CREATE TRIGGER geocaching_points_evw_update INSTEAD OF UPDATE ON geocaching_points_evw BEGIN UPDATE OR IGNORE geocaching_points SET gdb_to_date = julianday('now') WHERE OBJECTID = OLD.OBJECTID AND gdb_to_date BETWEEN (julianday ('9999-12-31 23:59:59') - 0.000000001) AND (julianday ('9999-12-31 23:59:59') + 0.000000001);REPLACE INTO geocaching_points (OBJECTID,Shape,Name,FolderPath,SymbolID,AltMode,Base,Snippet,PopupInfo,HasLabel,LabelID,gdb_from_date,gdb_to_date) VALUES (NEW.OBJECTID,NEW.Shape,NEW.Name,NEW.FolderPath,NEW.SymbolID,NEW.AltMode,NEW.Base,NEW.Snippet,NEW.PopupInfo,NEW.HasLabel,NEW.LabelID,(SELECT MAX (gdb_to_date) FROM geocaching_points WHERE OBJECTID = OLD.OBJECTID AND gdb_to_date < julianday ('9999-12-31 23:59:59')),julianday ('9999-12-31 23:59:59')); END;;
